import{_ as e,o as t,c as o,Q as l}from"./chunks/framework.3991f943.js";const b=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en/features.md","filePath":"en/features.md"}'),i={name:"en/features.md"},n=l('<h2 id="basic" tabindex="-1">Basic <a class="header-anchor" href="#basic" aria-label="Permalink to &quot;Basic&quot;">​</a></h2><ul><li><code>Translation Service</code><ul><li><code>LibreTranslate</code> - API Key is not required, slow, inaccurate.</li><li><code>Google Translate</code> - API Key is optional, fast, accurate.</li></ul></li><li><code>API Key</code> - Enter your API key for the translation service you selected. Some services don&#39;t require an API key.</li><li><code>Background Color</code> - Select the background color of the page. This is useful for <strong>Chroma Keying</strong></li><li><code>Server Mode</code> - <strong>[APP ONLY]</strong><ul><li><code>ON</code> - The app will run as a server, and you can connect to it via the <code>/client</code> endpoint.</li><li><code>OFF</code> - The app will run normally. Browser source will not work.</li></ul></li><li><code>Input Device</code> - <strong>[NOT IMPLEMENTED]</strong> Select the input device you&#39;d like to use. This is useful if you have multiple microphones.</li><li><code>Output Device</code> - <strong>[NOT IMPLEMENTED]</strong> Select the output device you&#39;d like to use. This is useful if you have multiple speakers.</li></ul><h2 id="subtitle-translations" tabindex="-1">Subtitle &amp; Translations <a class="header-anchor" href="#subtitle-translations" aria-label="Permalink to &quot;Subtitle &amp; Translations&quot;">​</a></h2><ul><li><code>Color</code> - Select the color of the subtitle / translation text.</li><li><code>Border</code> - Select the border color of the subtitle / translation text.</li><li><code>Size</code> - Select the size of the subtitle / translation text.</li><li><code>Weight</code> - Select the width of the subtitle / translation text.</li><li><code>Border Size</code> - Select the border size of the subtitle / translation text.</li><li><code>Language</code><ul><li>For <code>SUBTITLE</code>, select the language you will be speaking in.</li><li>For <code>TRANSLATION</code>, select the language you want the subtitle to be translated to.</li></ul></li><li><code>Font</code> - Select the font of the subtitle / translation text.</li></ul><h2 id="text" tabindex="-1">Text <a class="header-anchor" href="#text" aria-label="Permalink to &quot;Text&quot;">​</a></h2><ul><li><code>Horizontal</code> - Select the horizontal position of the text. <ul><li><code>Left</code> - The text will be aligned to the left.</li><li><code>Center</code> - <strong>[DEFAULT]</strong> The text will be aligned to the center.</li><li><code>Right</code> - The text will be aligned to the right.</li></ul></li><li><code>Vertical</code> - Select the vertical position of the text. <ul><li><code>Top</code> - The text will be aligned to the top.</li><li><code>Bottom</code> - <strong>[DEFAULT]</strong> The text will be aligned to the bottom.</li></ul></li><li><code>White Space</code> - Select whether or not to break long sentences into new lines. <ul><li><code>NORMAL</code> - <strong>[DEFAULT]</strong> Long sentences will be broken into new lines.</li><li><code>NO WRAP</code> - Long sentences will not be broken into new lines.</li></ul></li><li><code>Delete Timer</code> (ms) - Select the amount of time to wait before deleting the text. <ul><li><code>0</code> - <strong>[DEFAULT]</strong> The text will not be deleted.</li><li><code>100-5000</code> - The text will be deleted after the specified amount of time.</li></ul></li><li><code>New Sentence Timer</code> (ms) - Select the amount of time to wait before starting a new sentence. <ul><li><code>0</code> - Wait until the server detects the end of a sentence. This may break the app.</li><li><code>100-1000</code> - Amount of silence to wait for before starting a new sentence.</li><li><code>1000</code> - <strong>[DEFAULT]</strong></li></ul></li><li><code>Show Language</code> - Select whether or not to show the language of the translations. <ul><li><code>ON</code> - The language will be shown.</li><li><code>OFF</code> - <strong>[DEFAULT]</strong> The language will not be shown.</li></ul></li><li><code>Bouyomi-chan</code> - <strong>[NOT IMPLEMENTED]</strong> Select whether or not to use the Bouyomi-chan protocol. <ul><li><code>ON</code> - The Bouyomi-chan protocol will be used.</li><li><code>OFF</code> - <strong>[DEFAULT]</strong> The Bouyomi-chan protocol will not be used.</li></ul></li><li><code>Censor Words</code> - <strong>[NOT IMPLEMENTED]</strong> Select whether or not to censor profanity. <ul><li><code>ON</code> - Words will be censored.</li><li><code>OFF</code> - <strong>[DEFAULT]</strong> Words will not be censored.</li></ul></li></ul>',6),c=[n];function r(a,s,d,u,h,g){return t(),o("div",null,c)}const f=e(i,[["render",r]]);export{b as __pageData,f as default};
