import{_ as e,o,c as t,Q as a}from"./chunks/framework.3991f943.js";const f=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en/guide.md","filePath":"en/guide.md"}'),i={name:"en/guide.md"},l=a('<h2 id="1" tabindex="-1">Browser Application <a class="header-anchor" href="#1" aria-label="Permalink to &quot;Browser Application {#1}&quot;">​</a></h2><h3 id="1-2" tabindex="-1">Web-App Usage <a class="header-anchor" href="#1-2" aria-label="Permalink to &quot;Web-App Usage {#1-2}&quot;">​</a></h3><p><strong>OBS will not capture the subtitles if the window is minimized.</strong><br><strong>If you want to fix this, you can use the <a href="#2">app version</a> of liveSubs.</strong><br></p><ol><li>Go to <a href="https://subs.puv.bar" target="_blank" rel="noreferrer">https://subs.puv.bar</a></li><li>Allow the use of your microphone by clicking <code>Allow</code> in the top left corner</li><li>Select the Language you will be speaking in. The selection can be made in the <code>Language</code> column of the <code>Subtitle</code> row</li><li>Add translation options by clicking <code>Add Translation</code>. You can add up to 5 translation options <ol><li>Select the language for each of the translation options</li></ol></li><li>When you&#39;re done changing the settings, click the <code>Close</code> button</li></ol><p><strong>Next -&gt; <a href="#1-3">OBS Setup</a></strong></p><h3 id="1-3" tabindex="-1">Web-app OBS setup <a class="header-anchor" href="#1-3" aria-label="Permalink to &quot;Web-app OBS setup {#1-3}&quot;">​</a></h3><ol><li>In the recording software of your choosing, add a new <code>Window Capture</code> source</li><li>Select the window with the title <code>liveSubs</code> and click <code>OK</code></li><li>Click <code>OK</code></li></ol><p><strong>Next -&gt; <a href="#3">Background Removal</a></strong></p><h2 id="2" tabindex="-1">Windows Application <a class="header-anchor" href="#2" aria-label="Permalink to &quot;Windows Application {#2}&quot;">​</a></h2><h3 id="2-1" tabindex="-1">App Installation <a class="header-anchor" href="#2-1" aria-label="Permalink to &quot;App Installation {#2-1}&quot;">​</a></h3><p><strong>You may get a warning from your browser or Windows because the app is not signed.</strong><br><strong>This application is safe to use, and you can check the source code <a href="https://github.com/puv/liveSubs" target="_blank" rel="noreferrer">here</a>.</strong><br><strong>It would be very nice if you could click <code>&quot;Report as safe&quot;</code>, as it would help the project.</strong><br></p><ol><li>Go to <a href="https://github.com/puv/liveSubs/releases/latest" target="_blank" rel="noreferrer">https://github.com/puv/liveSubs/releases/latest</a></li><li>Download the <code>latest version</code> of liveSubs <ol><li>If you get a warning from your browser, click <code>...</code> or <code>More</code>, and then click <code>Keep</code></li><li>If asked for confirmation, click <code>Keep anyway</code></li></ol></li><li>Open the downloaded file <ol><li>If you get a warning from Windows, click <code>More info</code>, and then click <code>Run anyway</code></li></ol></li></ol><p><strong>Next -&gt; <a href="#2-2">Windows Application Usage</a></strong></p><h3 id="2-2" tabindex="-1">Win-App Usage <a class="header-anchor" href="#2-2" aria-label="Permalink to &quot;Win-App Usage {#2-2}&quot;">​</a></h3><ol><li>Open the downloaded file</li><li>Allow the use of your microphone by clicking <code>Allow</code> in the top left corner</li><li>Select the Language you will be speaking in. The selection can be made in the <code>Language</code> column of the <code>Subtitle</code> row</li><li>Add translation options by clicking <code>Add Translation</code>. You can add up to 5 translation options <ol><li>Select the language for each of the translation options</li></ol></li><li>When you&#39;re done changing the settings, click the <code>Close</code> button</li></ol><p><strong>Next -&gt; <a href="#2-3">OBS Setup</a></strong></p><h3 id="2-3" tabindex="-1">Win-App OBS setup <a class="header-anchor" href="#2-3" aria-label="Permalink to &quot;Win-App OBS setup {#2-3}&quot;">​</a></h3><ol><li>In the recording software of your choosing, add a new <code>Browser Source</code> source</li><li>For the URL, write <code>https://subs.puv.bar/client</code>, and click <code>Close</code></li><li>Click <code>OK</code></li></ol><p><strong>Next -&gt; <a href="#3">Background Removal</a></strong></p><h2 id="3" tabindex="-1">Background removal <a class="header-anchor" href="#3" aria-label="Permalink to &quot;Background removal {#3}&quot;">​</a></h2><ol start="2"><li>Right-click the <code>Window Capture</code> source you just added and click <code>Filters</code> -&gt; <code>Edit Filters</code></li><li>Click the <code>+</code> button, select <code>Chroma Key</code>, and click <code>Add</code></li><li>Click on the color picker and select the color of the background of the subtitles. By default, it&#39;s green</li><li>Click <code>Close</code>.</li></ol>',21),n=[l];function c(r,d,s,h,u,p){return o(),t("div",null,n)}const b=e(i,[["render",c]]);export{f as __pageData,b as default};
