import{_ as a,o as l,c as e,Q as i}from"./chunks/framework.3991f943.js";const g=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"ja/guide.md","filePath":"ja/guide.md"}'),r={name:"ja/guide.md"},t=i('<h2 id="1" tabindex="-1">ブラウザ アプリケーション <a class="header-anchor" href="#1" aria-label="Permalink to &quot;ブラウザ アプリケーション {#1}&quot;">​</a></h2><h3 id="1-2" tabindex="-1">ウェブアプリの使用法 <a class="header-anchor" href="#1-2" aria-label="Permalink to &quot;ウェブアプリの使用法 {#1-2}&quot;">​</a></h3><p><strong>ウィンドウが最小化されている場合、OBS は字幕をキャプチャしません。</strong><br><strong>これを修正したい場合は、liveSubs の <a href="#2">アプリ バージョン</a> を使用できます。</strong><br></p><ol><li><a href="https://subs.puv.bar" target="_blank" rel="noreferrer">https://subs.puv.bar</a> に移動します</li><li>左上隅の「許可」をクリックしてマイクの使用を許可します。</li><li>話す言語を選択します。選択は、「字幕」行の「言語」列で行うことができます。</li><li>「翻訳を追加」をクリックして、翻訳オプションを追加します。 最大 5 つの翻訳オプションを追加できます <ol><li>各翻訳オプションの言語を選択します</li></ol></li><li>設定の変更が完了したら、「閉じる」ボタンをクリックします。</li></ol><p><strong>次へ -&gt; <a href="#1-3">OBS セットアップ</a></strong></p><h3 id="1-3" tabindex="-1">Web アプリ OBS セットアップ <a class="header-anchor" href="#1-3" aria-label="Permalink to &quot;Web アプリ OBS セットアップ {#1-3}&quot;">​</a></h3><ol><li>選択した録音ソフトウェアで、新しい「ウィンドウ キャプチャ」ソースを追加します。</li><li>タイトル「liveSubs」のウィンドウを選択し、「OK」をクリックします。</li><li>「OK」をクリックします。</li></ol><p><strong>次へ -&gt; <a href="#3">背景除去</a></strong></p><h2 id="2" tabindex="-1">Windows アプリケーション <a class="header-anchor" href="#2" aria-label="Permalink to &quot;Windows アプリケーション {#2}&quot;">​</a></h2><h3 id="2-1" tabindex="-1">アプリのインストール <a class="header-anchor" href="#2-1" aria-label="Permalink to &quot;アプリのインストール {#2-1}&quot;">​</a></h3><p><strong>アプリが署名されていないため、ブラウザまたは Windows から警告が表示される場合があります。</strong><br><strong>このアプリケーションは安全に使用でき、ソース コードは <a href="https://github.com/puv/liveSubs" target="_blank" rel="noreferrer">ここ</a> で確認できます。</strong><br><strong>プロジェクトの助けになりますので、「安全であると報告」をクリックしていただければ幸いです。</strong><br></p><ol><li><a href="https://github.com/puv/liveSubs/releases/latest" target="_blank" rel="noreferrer">https://github.com/puv/liveSubs/releases/latest</a> に移動します。</li><li>liveSubs の「最新バージョン」をダウンロードします。 <ol><li>ブラウザから警告が表示された場合は、「...」または「詳細」をクリックし、「維持」をクリックします。</li><li>確認を求められたら、「とにかく保持する」をクリックします。</li></ol></li><li>ダウンロードしたファイルを開きます <ol><li>Windows から警告が表示された場合は、「詳細」をクリックし、「とにかく実行」をクリックします。</li></ol></li></ol><p><strong>次へ -&gt; <a href="#2-2">Windows アプリケーションの使い方</a></strong></p><h3 id="2-2" tabindex="-1">Win アプリの使用状況 <a class="header-anchor" href="#2-2" aria-label="Permalink to &quot;Win アプリの使用状況 {#2-2}&quot;">​</a></h3><ol><li>ダウンロードしたファイルを開きます</li><li>左上隅の「許可」をクリックしてマイクの使用を許可します。</li><li>話す言語を選択します。選択は、「字幕」行の「言語」列で行うことができます。</li><li>「翻訳を追加」をクリックして、翻訳オプションを追加します。 最大 5 つの翻訳オプションを追加できます <ol><li>各翻訳オプションの言語を選択します</li></ol></li><li>設定の変更が完了したら、「閉じる」ボタンをクリックします。</li></ol><p><strong>次へ -&gt; <a href="#2-3">OBS セットアップ</a></strong></p><h3 id="2-3" tabindex="-1">Win-App OBS セットアップ <a class="header-anchor" href="#2-3" aria-label="Permalink to &quot;Win-App OBS セットアップ {#2-3}&quot;">​</a></h3><ol><li>選択した録音ソフトウェアで、新しい「ブラウザ ソース」ソースを追加します。</li><li>URL に「<a href="https://subs.puv.bar/client%E3%80%8D%E3%81%A8%E5%85%A5%E5%8A%9B%E3%81%97%E3%80%81%E3%80%8C%E9%96%89%E3%81%98%E3%82%8B%E3%80%8D%E3%82%92%E3%82%AF%E3%83%AA%E3%83%83%E3%82%AF%E3%81%97%E3%81%BE%E3%81%99%E3%80%82" target="_blank" rel="noreferrer">https://subs.puv.bar/client」と入力し、「閉じる」をクリックします。</a> 1.「OK」をクリックします。</li></ol><p><strong>次へ -&gt; <a href="#3">背景除去</a></strong></p><h2 id="3" tabindex="-1">背景の除去 <a class="header-anchor" href="#3" aria-label="Permalink to &quot;背景の除去 {#3}&quot;">​</a></h2><ol start="2"><li>追加した「Window Capture」ソースを右クリックし、「Filters」 -&gt; 「Edit Filters」をクリックします。</li><li>「+」ボタンをクリックし、「クロマキー」を選択し、「追加」をクリックします。</li><li>カラーピッカーをクリックして、字幕の背景の色を選択します。 デフォルトでは緑色です</li><li>「閉じる」をクリックします。</li></ol>',21),o=[t];function s(n,h,d,u,p,b){return l(),e("div",null,o)}const _=a(r,[["render",s]]);export{g as __pageData,_ as default};
